import React, {useState,useEffect} from 'react';
//import Menu from './Menu';
//import {getCustomers,addCustomer,deleteCustomer,updateCustomer, getCustomerById} from '../service/CustomerServiceAPI';
import {getCustomers,addCustomer,deleteCustomer,updateCustomer, getCustomerById} from '../../service/CustomerServiceAPI';
// function 
//export default About;
export default function CustomerFuncApp({history,match}) { //p 46
   //
   // destructure useState
   const [items,setItems] = useState(getCustomers());
    const [id,setId] = useState(0);
    const [name,setName] = useState("");
    const [email,setEmail] = useState("");
    const [phone,setPhone] = useState("");
    const [address,setAddress] = useState("");
    const [label,setLabel] = useState("Add");

    useEffect(async ()=>{
        if(match.params.id){ //edit
            let customer = await getCustomerById(match.params.id);
            setId(customer.id);
            setName(customer.name);
            setEmail(customer.email);
            setPhone(customer.phone);
            setAddress(customer.address);
            setLabel("Update");
        }
    },[]);
  var handleChange = (e) => {
      if(e.target.name == "name"){
        setName(e.target.value);
      }else if(e.target.name == "email"){
        setEmail(e.target.value);
      }else if(e.target.name == "phone"){
        setPhone(e.target.value);
      }else if(e.target.name == "address"){
        setAddress(e.target.value);
    }
  }
    var handleSubmit = async (e) =>  {
      e.preventDefault();
      if (!name.length) {
        return;
      }
      const newItem = {
          name: name,
          email:email,
          phone:phone,
          address: address,
          id:Date.now()
      };
      if(id != 0){ //edit
        newItem.id = id;
        await updateCustomer(newItem);
      }else{ //new
        await addCustomer(newItem);
        //tempItems = items.concat(newItem);
      }/*
        setId(0);
        setName("");
        setEmail("");
        setPhone("");
        setAddress("");
        setLabel("Add");
        setItems(getCustomers());*/
        //history.push("/customer");
    }
    return (
      <div>
          {/* <Menu/> */}
        <h3>Customers</h3>
        <form>
          <input name='name' placeholder="name"
            onChange={handleChange}
            value={name}
          /> <br/><br/>
          <input name='email' placeholder="email"
            onChange={handleChange}
            value={email}
          /> <br/><br/>
          <input name='phone' placeholder="phone"
            onChange={handleChange}
            value={phone}
          /> <br/><br/>
          <input name='address' placeholder="address"
            onChange={handleChange}
            value={address}
          /> <br/><br/>
          <button onClick={handleSubmit}> 
            {label}
          </button>
          <div>
            <Card>
                <CardTitle className="border-bottom p-3 mb-0" style={{ color: 'white', backgroundColor: 'rgb(69, 124, 160)', fontSize: '21px' }}>
                    <b>Customers</b>
                </CardTitle>
                <CardBody style={{padding:10}}>
                 <Table striped bordered hover>
                           <thead style={{fontSize:14.5}}>
                                <tr style={{ color: 'rgb(69, 124, 160)' }}>
                                    <th style={{ whiteSpace: 'nowrap',zIndex:1 }}>ID</th>
                                    <th style={{ whiteSpace: 'nowrap',zIndex:1  }}>Name</th>
                                    <th style={{ whiteSpace: 'nowrap' ,zIndex:1 }}>Email</th>
                                    <th style={{ whiteSpace: 'nowrap',zIndex:1  }}>phone</th>
                                    <th style={{ whiteSpace: 'nowrap' ,zIndex:1 }}>address</th>
                                </tr>
                            </thead>
                            <tbody>
                                {sessions.length > 0 && !loading ? sessions.map((item,indx) => (
                                    <tr key={indx} style={item.isRinse == 0 || item.isRinse ==undefined ? { color: 'black'} : item.isFinished == 0 ? { color: 'black',backgroundColor:'#48C9B0'} : {color: 'black',backgroundColor:'#EF99AB'}}>
                                        <td>{item.id}</td>
                                        <td>{item.name}</td>
                                        <td>{item.email}</td>
                                        <td>{item.phone}</td>
                                        <td>{item.address}</td>
                                    </tr>
                                )): !loading ? <tr style={{padding:20,paddingLeft:10, fontSize:20, color: 'rgb(69, 124, 160)',backgroundColor:"white" }}><td>No data found.</td></tr>:<span/>}
                            </tbody>
                    </Table>
                </CardBody>
            </Card>
        </div>
    );
}

        </form>
        
        </div>
    );
  }
